import React from 'react';
import LeftOn from '../../assets/Left-black.png';
import LeftOff from '../../assets/Left-gray.png';
import RightOn from '../../assets/Right-black.png';
import RightOff from '../../assets/Right-gray.png';
import Styled from 'styled-components';

const CalendarWrap = Styled.div`
  .calendar {
    width: 1200px;
    height: 118px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    
    &__year {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: flex-end;
      height: 61px;

      &--left:hover, &--right:hover {
        cursor: pointer;
      }

      &--number {
        font-size: 36px;
        font-weight: bold;
        margin: 0 25px;
        line-height: 1;
      }
    }

    &__month {
      height: 57px;
      display: flex;
      flex-direction: row;
      justify-content: space-between;
      align-items: center;
      width: 1025px;

      &--button {
        font-size: 18px;
        width: 52px;
        &:hover {
          font-size: 22px;
          font-weight: bold;
          cursor: pointer;
        }
      }
    }
  }
`;

const Calendar = ({currYear,setCurrYear,currMonth,setCurrMonth}) => {
    return(
        <div className="calendar">Calendar
        <div className="calendar__year"></div>
            <img className="calendar__year--left" src={LeftOff} alt="" />
            {currYear}
            <img className="calendar__year--right" src={LeftOn} alt="" />
        <div className="calendar__month"></div>
        </div>
    );
}

export default Calendar;
